import Router from 'vue-router'
import Vue from 'vue'
Vue.use(Router)
const router=new Router({
    mode:'history',
    routes:[
        {
            path:'/TourismManage',
            name:'TourismManage',
            meta:{
                requireAuth:true
            },
            component:()=>import('../components/TourismManage'),
            children:[
                {
                    path:'/HomePage',
                    name:'HomePage',
                    meta:{
                        requireAuth:true
                    },
                    component:()=>import('../components/HomePage/HomePage')
                },
                {
                    path:'/UserManage',
                    name:'UserManage',
                    meta:{
                        requireAuth:true
                    },
                    component:()=>import('../components/UserManage/UserManage')
                },
                {
                    path:'/GoodsManage',
                    name:'GoodsManage',
                    meta:{
                        requireAuth:true
                    },
                    component:()=>import('../components/GoodsManage/GoodsManage')
                },
                {
                    path:'/HeritageManage',
                    name:'HeritageManage',
                    meta:{
                        requireAuth:true
                    },
                    component:()=>import('../components/HeritageManage/HeritageManage')
                },
                {
                    path:'/OrdersManage',
                    name:'OrdersManage',
                    meta:{
                        requireAuth:true
                    },
                    component:()=>import('../components/OrdersManage/OrdersManage')
                },
            ]
        }
    ]
})

export default  router
