import Router from 'vue-router'
import Vue from 'vue'
Vue.use(Router)
const router=new Router({
    mode:'history',
    routes:[
        {
            path:'/TourismManage',
            name:'TourismManage',
            meta:{
                requireAuth:true
            },
            component:()=>import('../components/TourismManage'),
            /*
            children:[
                {
                    path:'/UserManage',
                    name:'UserManage',
                    meta:{
                        requireAuth:true
                    },
                    component:()=>import('../components/UserManage/UserManageView')
                },
                {
                    path:'/BillManage',
                    name:'BillManage',
                    meta:{
                        requireAuth:true
                    },
                    component:()=>import('../components/BillManage/BillManageView')
                },
                {
                    path:'/HomePage',
                    name:'HomePage',
                    meta:{
                        requireAuth:true
                    },
                    component:()=>import('../components/HomePage/HomePage')
                },
            ]

             */
        }
    ]
})

export default  router
