import axios from "axios";
class UserApi{
    searchUser(pageSize,pageNum,userName,userAccount,userGroup){
        return axios.post('http://localhost:8082/user/selectPage',{
            pageSize:pageSize,
            pageNum:pageNum,
            param:{
                userName:userName,
                userAccount:userAccount,
                userGroup:userGroup,
            },
        })
    }

    delUser(userID){
        return axios.get('http://localhost:8082/user/deleteByID?userID='+userID)
    }

    addUser(user){
        return axios.post('http://localhost:8082/user/saveUser',user)
    }

}
export default new UserApi()
