import axios from "axios";
const urlPrefix='http://localhost:8001/'
class UserApi{
    searchUser(pageSize,pageNum,type,search){
        return axios.post(urlPrefix+'/userSys/selectPage',{
            pageSize:pageSize,
            pageNum:pageNum,
            param:{
                type:type,
                search:search,
            },
        })
    }

    delUser(user){
        return axios.post(urlPrefix+'/userSys/delUser',user)
    }

    updateUser(user){
        return axios.post(urlPrefix+'/userSys/updateUser',user)
    }

    userLogin(userAccount,userPassword){
        return axios.get(urlPrefix+'/userSys/userLogin?userAccount='+userAccount+'&userPassword='+userPassword)
    }

    userRegister(user){
        return axios.post(urlPrefix+'/userSys/userRegister',user)
    }

}
export default new UserApi()
