            pageSize:pageSize,
            pageNum:pageNum,
            param:{
                type:type,
                search:search,
            },
        })
    }

    delGoods(heritage){
        return axios.post(urlPrefix+'/goodsSys/delGoods',heritage)
    }

    addGoods(heritage){
        return axios.post(urlPrefix+'/goodsSys/addGoods',heritage)
    }

    updateGoods(heritage){
        return axios.post(urlPrefix+'/goodsSys/updateGoods',heritage)
    }

    delGoodsImg(fileName){
        return axios.get(urlPrefix+'/goodsSys/delGoodsImg?fileName='+fileName)
    }

}
