import axios from "axios";
class UserApi{
    selectPage(pageSize,pageNum,type,search){
        return axios.post('http://localhost:8001/userSys/selectPage',{
            pageSize:pageSize,
            pageNum:pageNum,
            param:{
                type:type,
                search:search,
            },
        })
    }

    delUser(userID){
        return axios.get('http://localhost:8082/user/deleteByID?userID='+userID)
    }

    addUser(user){
        return axios.post('http://localhost:8082/user/saveUser',user)
    }

}
export default new UserApi()
