import axios from "axios";
class HeritageApi{
    searchHeritage(pageSize,pageNum,type,search){
        return axios.post('http://localhost:8001/heritageSys/selectPage',{
            pageSize:pageSize,
            pageNum:pageNum,
            param:{
                type:type,
                search:search,
            },
        })
    }

    delHeritage(heritage){
        return axios.post('http://localhost:8001/heritageSys/delHeritage',heritage)
    }

    addHeritage(heritage){
        return axios.post('http://localhost:8001/heritageSys/addHeritage',heritage)
    }

    updateHeritage(heritage){
        return axios.post('http://localhost:8001/heritageSys/updateHeritage',heritage)
    }

    delHeritageImg(fileName){
        return axios.get('http://localhost:8001/heritageSys/delHeritageImg&fileName='+fileName)
    }

}
export default new HeritageApi()
